import React from "react";
import { View, Text } from "react-native";
import MyButton from "./components/MyButton";
import Counter from "./components/Counter";

const App = () => {
  return (
    <View
      style={{
        flex: 1,
        backgroundColor: "#fff",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Text style={{ fontSize: 30, marginBottom: 10 }}>Props</Text>
      {/* 1. 부모 → 자식 */}
      <MyButton
        title="Button"
        onPress={() => {
          alert("props");
        }}
      />
      {/* 2. 컴포넌트 태그 사이의 값 전달 */}
      <MyButton
        title="Button"
        onPress={() => {
          alert("props");
        }}
      >
        Chlidren Props
      </MyButton>
      {/* 3. defaultProps */}
      <MyButton />

      <Counter />
    </View>
  );
};

export default App;
